            t = stoi(track.substr(idx+1,2));
            idx += 3;
            if (tail &lt; 0) head = t;
            else total += abs(tail-t);
            tail = t;
        } else if (track[idx]=='L') {
            t = stoi(track.substr(idx+1,1));
