#define N 105

int n,mod;
int v[N][N];

int fpow(int x,int y){
    if(!y)return 1;
    if(y&amp;1)return fpow(x,y-1)*x%mod;
    int z=fpow(x,y/2)%mod;
    return z*z%mod;
}

void out(){
    cout&lt;&lt;nnnn;
    for(int i=1;i&lt;=n;i++){
        for(int j=1;j&lt;=n+1;j++){
            cout&lt;&lt;v[i][j]&lt;&lt;nnn;
        }
        cout&lt;&lt;nn;
    }
    cout&lt;&lt;nnnn;
}

