    ios::sync_with_stdio(0);
    cin.tie(0);
    //istringstream cin("3 101 \
69 59 89 \
5 4 2 \
3 6 7 \
2 0 1");
    cin&gt;&gt;n&gt;&gt;mod;

    for(int i=1;i&lt;=n+1;i++){//​存入​陣列​
        for(int j=1;j&lt;=n;j++){
            cin&gt;&gt;v[j][n-i+2];
        }
    }

    for(int i=1;i&lt;=n;i++){

        //​例外​狀況​交換​(​目標​為​0)
        if(v[i][i]==0){
            for(int j=i;j&lt;=n;j++){//​往後找​不是​0​的​
                if(v[j][i]!=0){//​找到​不是​0​的​
                    for(int k=1;k&lt;=n+1;k++){//​交換​
                        swap(v[i][k],v[j][k]);
                    }
                    break;
                }
            }
        }

        //​第​i​個​變成​1
        int e=fpow(v[i][i],mod-2);
        for(int j=i;j&lt;=n+1;j++){//​前面​都​是​0​了​，​往後乘模​逆元​就​好​
            v[i][j]=v[i][j]*e%mod;
        }

        //​其他​變成​0
        for(int j=1;j&lt;=n;j++){
            if(j==i)continue;//​跳過​自己​

            int tmp=v[j][i];//​要​先存​起來​不然​會​被​改掉​
            for(int k=i;k&lt;=n+1;k++){
                v[j][k]= ((v[j][k] - (v[i][k]*tmp))%mod+mod)%mod;
            }
        }

    }

    for(int i=n;i&gt;=1;i--)cout&lt;&lt;v[i][n+1]&lt;&lt;" ";
}
